:root {
  --accent: #9b59b6;
  --accent-light: #be78ff;
  --background: #0a0a0a;
  --foreground: #ededed;
  --block-bg: rgba(21, 21, 21, 0.7);
  --block-border: rgba(155, 89, 182, 0.2);
  --mention-bg: rgba(155, 89, 182, 0.15);
  --hover-bg: rgba(155, 89, 182, 0.05);
  --scrollbar-thumb: #9b59b6;
}

body {
  background-color: var(--background);
  color: var(--foreground);
  font-family: 'Inter', sans-serif;
  line-height: 1.6;
  transition: background-color 0.3s ease, color 0.3s ease;
  animation: fadeIn 0.4s ease-out forwards;
}

.block, .message-box {
  background: var(--block-bg);
  backdrop-filter: blur(25px);
  border: 1px solid var(--block-border);
  padding: 1.25rem;
  border-radius: 10px;
  margin-bottom: 1.5rem;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.25);
  animation: slideUpFadeIn 0.5s ease-out forwards;
  opacity: 0;
  transform: translateY(12px);
}

.block:hover, .message-box:hover {
  box-shadow: 0 4px 16px rgba(155, 89, 182, 0.3);
  transform: translateY(-2px);
  transition: box-shadow 0.3s ease, transform 0.3s ease;
}

.mention {
  background-color: var(--mention-bg);
  border-left: 3px solid var(--accent);
  padding: 0.4rem 0.75rem;
  border-radius: 4px;
  font-weight: 600;
  color: var(--accent);
  display: inline-block;
  animation: pulseGlow 3s ease-in-out infinite;
}

a, button {
  color: var(--accent);
  cursor: pointer;
  font-weight: 600;
  text-decoration: none;
  position: relative;
  transition: color 0.2s ease;
  animation: fadeIn 0.6s ease-out forwards;
}

a:hover, button:hover, a:focus, button:focus {
  color: #d3b3ff;
  text-shadow: 0 0 4px var(--accent);
  animation: glowPulse 1.5s ease-in-out infinite alternate;
}

::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: var(--scrollbar-thumb);
  border-radius: 10px;
  box-shadow: inset 0 0 6px rgba(155, 89, 182, 0.4);
  transition: background 0.3s ease;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--accent-light);
}

*::selection {
  background-color: var(--accent);
  color: #fff;
}

